mapper.select:
select ctno ,category, nickname, passwd, deal_way, deal_code, product_code, hprice, region, tel, email, quantity, title, content, purc_date, wdate, cnt, file1, file2, size2, file3, file4, size4, file5, file6, size6, file7, file8, size8, file9, file10, size10, userid
from camera
where ctno = #{ctno}

select nickname, email
from member 
where userid=#{userid}

mapper.update:
update camera
set category=#{category}, nickname=#{nickname}, deal_way=#{deal_way}, deal_code=#{deal_code}, product_code=#{product_code}, hprice=#{hprice}, region=#{region}, tel=#{tel}, email=#{email}, quantity=#{quantity}, title=#{title}, content=#{content}, purc_date=#{purc_date},file1=#{file1}, file2=#{file2}, size2=#{size2}, file3=#{file3}, file4=#{file4}, size4=#{size4}, file5=#{file5}, file6=#{file6}, size6=#{size6}, file7=#{file7}, file8=#{file8}, size8=#{size8}, file9=#{file9}, file10=#{file10}, size10=#{size10} 
where ctno = #{ctno}

update camera 
set cnt = cnt + 1
where ctno = #{ctno}

mapper.select:
SELECT ctno, deal_code, title, hprice, deal_way, region, nickname, wdate,cnt, file1, file2, size2, r
from (
    SELECT ctno, deal_code, title, hprice, deal_way, region, nickname, wdate,cnt, file1, file2, size2, rownum as r
    from (
        SELECT ctno, deal_code, title, hprice, deal_way, region, nickname, wdate,cnt, file1, file2, size2  
        FROM camera
    
    
       where title LIKE '%' || #{word} || '%' 
    
    
       where content LIKE '%' || #{word} || '%' 
    
    
       where category LIKE '%' || #{word} || '%' 
    
    
       where title LIKE '%' || #{word} || '%'  OR content LIKE '%' || #{word} || '%' 
          
  
    ORDER BY ctno DESC
    )
  )
  WHERE r >=#{startNum} AND r <= #{endNum}
