mapper.update:
UPDATE CHEAT
SET title =
#{title}, gubun =#{gubun}, region = #{region}, occurday =
#{occurday}
, buyprice = #{buyprice}, cheatid = #{cheatid}, cheattel = #{cheattel}
, cheatemail = #{cheatemail}, cnt = 0, content = #{content}
, email = #{email}, tel = #{tel}, userid = #{userid}, nickname =
#{nickname}, passwd = #{passwd}
, thumb = #{thumb}, file1 = #{file1} , size1 = #{size1}, file2 =
#{file2}, size2 = #{size2}, file3 = #{file3}, size3 = #{size3},
file4 = #{file4}, size4 = #{size4}, file5 = #{file5}, size5 =
#{size5}
WHERE ctno = #{ctno} and passwd = #{passwd}

mapper.select:
SELECT COUNT(*) as cnt
FROM cheat
WHERE ctno=#{ctno} AND passwd=#{passwd}

mapper.update:
UPDATE cheat
SET cnt = cnt + 1
WHERE ctno = #{ctno}
