mapper.select:
SELECT COUNT(userid) as cnt
FROM member
WHERE userid=#{userid}

SELECT COUNT(userid) as cnt
FROM member
WHERE nickname=#{nickname}

SELECT COUNT(userid) as cnt
FROM member
WHERE email=#{email}

SELECT COUNT(userid) as cnt
FROM member
WHERE nickname=#{nickname} and userid!=#{userid}

SELECT COUNT(userid) as cnt
FROM member
WHERE email=#{email} and userid!=#{userid}

mapper.update:
UPDATE member 
SET pwd=#{pwd}, nickname=#{nickname}, email=#{email}, tel=#{tel}, zipcode=#{zipcode},
      address1=#{address1}, address2=#{address2}, confirm=#{confirm}, act=#{act}
WHERE mno=#{mno}

UPDATE member 
SET dropout='Y', act='N'
WHERE userid=#{userid}

mapper.select:
SELECT COUNT(mno) as cnt
FROM member
WHERE userid=#{userid} AND pwd=#{pwd}

SELECT COUNT(mno) as cnt
FROM member
WHERE act=#{act}

mapper.update:
UPDATE member
SET confirm = 'Y', act = 'Y'
WHERE email=#{email} AND auth=#{auth}

mapper.select:
SELECT COUNT(userid) as cnt 
FROM member 
WHERE userid=#{userid} AND pwd=#{pwd}

SELECT userid, mno, pwd, name, nickname, email, tel, zipcode, address1, address2, mdate, 
                        auth, confirm, act, dropout, r
from (
    SELECT userid, mno, pwd, name, nickname, email, tel, zipcode, address1, address2, mdate, 
                        auth, confirm, act, dropout, rownum as r
    from (
        SELECT userid, mno, pwd, name, nickname, email, tel, zipcode, address1, address2, mdate, 
                        auth, confirm, act, dropout  
        FROM member
     
       where dropout= 'Y'
    
    ORDER BY mno asc
    )
  )
  WHERE r >=#{startNum} AND r <= #{endNum}

mapper.update:
UPDATE member
SET act=#{act}
WHERE mno=#{mno}
