mapper.update:
UPDATE notice
SET nickname = #{nickname}, title = #{title}, content = #{content}, file1 = #{file1}, file2 = #{file2}, size2 = #{size2}, userid=#{userid}
WHERE noticeno=#{noticeno}

mapper.select:
  SELECT noticeno, nickname, title, content, wdate, file1, file2, size2, cnt, userid
FROM notice 
 WHERE noticeno= #{noticeno}

mapper.update:
update notice
set cnt = cnt +1
where noticeno= #{noticeno}
