mapper.select:
select rno, nickname, rcomment, grpno, indent, ansnum, rdate, noticeno, userid 
from nt_REPLY
WHERE rno=#{rno}

mapper.update:
UPDATE nt_reply 
SET rcomment=#{rcomment}
WHERE grno=#{grno}

mapper.select:
select rno, nickname, rcomment, grpno, indent, ansnum, rdate, noticeno, userid, rownum as r
from (select rno, nickname, rcomment, grpno, indent, ansnum, rdate, noticeno, userid from nt_REPLY order by grpno desc, ansnum asc)
  WHERE noticeno=#{noticeno}

mapper.update:
update nt_reply
set cnt = cnt +1
where noticeno= #{noticeno}

UPDATE nt_reply
SET ansnum = ansnum + 1
WHERE grpno = #{grpno} AND ansnum > #{ansnum}
