mapper.select:
select nickname, email
from member 
where userid=#{userid}

mapper.update:
UPDATE reviews
SET r_cnt = r_cnt + 1
WHERE r_no=#{r_no}

UPDATE reviews 
SET passwd=#{passwd}, nickname=#{nickname}, seller_nick=#{seller_nick}, title=#{title}, t_category=#{t_category},
     content=#{content}, thumb=#{thumb}, file1=#{file1}, size1=#{size1}, file2=#{file2},size2=#{size2},
     file3=#{file3}, size3=#{size3}, file4=#{file4}, size4=#{size4}, file5=#{file5}, size5=#{size5}
WHERE r_no=#{r_no}
